import React, { useState, useEffect } from "react";
import { Modal, ModalBody } from "reactstrap";


// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col,
} from "reactstrap";

function AllProducts() {
  const [productname, setProductname] = useState("");
  const [enabled, setEnabled] = useState("");
  const [file, setFile] = useState("");
  const [message, setMessage] = useState("");
  
  const navigate = useNavigate();
  useEffect(() => {
    const isLoggedIn = localStorage.getItem('isLoggedIn');
    if (!isLoggedIn) {
      navigate('/login');
    }
  }, [navigate]);
  const fileInputRef = useRef(null);
  
  const handleAddProduct = async (e) => {
    try {
      e.preventDefault();
      const formData = new FormData();
      formData.append('productname', productname);
      formData.append('enabled', enabled);
      formData.append('file', file);

      const response = await fetch('http://localhost:5000/addproduct', {
        method: 'POST',
        body: formData,
      });

      if (response.status === 200) {
        setMessage("Product added successfully.");
        setProductname("");
        setEnabled("");
        setFile(null);
        
        if (fileInputRef.current) {
          fileInputRef.current.value = null;
        }
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || "An error occurred. Please try again.");
      }
    } catch (error) {
      setMessage(error.message);
    }
  }

  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:5000/products");
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  const [modalOpen, setModalOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
    setModalOpen(true);
  }; 
  
  const handle_add_product = () => {
    setModalOpen(true);
  };

  const toggleModal = () => {
    setModalOpen(!modalOpen);
  };


    return (
    <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <CardTitle tag="h4">All Products</CardTitle>
                <span
                  style={{ cursor: 'pointer', color: '#007bff', fontSize: '14px' }}
                   onClick={() => handle_add_product()} // replace with navigation
                >
                  + Add Product 
                </span>
              </div>
            </CardHeader>
            <CardBody>
              <Table className="tablesorter" responsive>
                <thead className="text-primary">
                  <tr>
                    <th>Product Name</th>
                    <th className="text-center">Stock Status</th>
                    <th>Image</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {products.length > 0 ? (
                    products.map((product) => (
                      <tr key={product._id}>
                        <td>{product.product_name}</td>
                        <td className="text-center">
                          {product.enabled === true || product.enabled === "yes"
                            ? "Active"
                            : "In-Active"}
                        </td>
                        <td>
                          
                          {product.image? (
                            <img
                              src={`http://localhost:5000/${product.image}`}
                              alt={product.product_name}
                              style={{ width: "50px", height: "50px", objectFit: "cover", cursor: "pointer" }}
                              onClick={() => handleImageClick(`http://localhost:5000/${product.image}`)}
                            />
                          ) : "N/A"}
                        </td>
                        <td></td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="4" className="text-center">No products found.</td>
                    </tr>
                  )}
                </tbody>
              </Table>
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Modal isOpen={modalOpen} toggle={toggleModal}>
        <modalHeader className="text-dark mt-2 ml-3" toggle={toggleModal}>
          Full Image View
        </modalHeader>
        <ModalBody className="text-center m-0 pt-1">
          {selectedImage && (
            <img
              src={selectedImage}
              alt="Full View"
              style={{ maxWidth: "auto", maxHeight: "50vh" }}
            />
          )}
        </ModalBody>
      </Modal>

      
      <Modal isOpen={modalOpen} toggle={toggleModal}>
        <modalHeader className="text-dark mt-2 ml-3" toggle={toggleModal}>
          Add Product
        </modalHeader>
        <ModalBody className="text-center m-0 pt-1">
          <div className="content">
        <Row>
          <Col md="6" className="mx-auto">
            <Card>
              <CardHeader>
                <h5 className="title">Add Product</h5>
              </CardHeader>
              <CardBody>
                <Form  enctype="multipart/form-data">
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <label>Product Name</label>
                        <Input
                          placeholder="Ex: abc, pqr, xyz"
                          type="text"
                          value={productname}
                          onChange={(e) => setProductname(e.target.value)}
                          id="productname"
                          name="productname"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>Enabled</label>
                          <Input
                            type="select"
                            className="form-control"
                            name="enabled"
                            id="enabled"
                            value={enabled}
                            onChange={(e) => setEnabled(e.target.value)}
                          >
                            <option value="" disabled>Choose an option</option>
                            <option value="yes">Yes</option>
                            <option value="no">No</option>
                          </Input>
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>Image</label>
                          <Input
                            type="file"
                            className="form-control"
                            name="file"
                            id="file"
                            innerRef={fileInputRef}
                            onChange={(e) => setFile(e.target.files[0])}
                            accept="image/*"
                          >
                            <option value="" disabled>Choose an option</option>
                            <option value="yes">Yes</option>
                            <option value="no">No</option>
                          </Input>
                      </FormGroup>
                    </Col>
                  </Row>
                </Form>
                {message && <div>{message}</div>}
              </CardBody>
              <CardFooter>
                <Button className="btn-fill" color="primary" onClick={handleAddProduct} type="submit">
                  Save
                </Button>
              </CardFooter>
            </Card>
