import React, { useState } from "react";
// import classNames from "classnames";
// import { Line, Bar } from "react-chartjs-2";
import axios from "axios";
import {
  Button,
  // ButtonGroup,
  Card,
  CardHeader,
  CardBody,
  // CardTitle,
  // DropdownToggle,
  // DropdownMenu,
  // DropdownItem,
  // UncontrolledDropdown,
  // Label,
  FormGroup,
  Form,
  Input,
  // Table,
  Row,
  Col,
  // UncontrolledTooltip,
  CardFooter,
} from "reactstrap";

// core components
// import {
//   chartExample1,
//   chartExample2,
//   chartExample3,
//   chartExample4,
// } from "variables/charts.js";

import bgImage from "assets/img/bg5.jpg";

function Login (props) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:3000/login', {username, password,});
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response?.data?.message || "Login Failed");
    }
  };

  return (
    <div className="content d-flex justify-content-center align-items-center" style={{ overflowX: "hidden", height: "100vh", backgroundImage: `url(${bgImage})`, backgroundSize: "cover", backgroundPosition: "center", backgroundRepeat: "no-repeat" }}>
      <Row>
        <Col md="6" className="text-center">
          <Card  style={{ width: "400px" }}>
            <CardHeader>
              <h5 >Welcome</h5>
            </CardHeader>
            <CardBody >
              <Form>
                <Row className="justify-content-center">
                  <Col className="pr-md-1" md="8">
                    <FormGroup >
                      <label>Username</label>
                      <Input className="mt-1"
                        defaultValue=""
                        placeholder="Username"
                        type="text">
                      </Input>
                    </FormGroup>
                  </Col>
                </Row>
                <Row className="justify-content-center">
                <Col className="pr-md-1" md="8">
                    <FormGroup >
                      <label>Password</label>
                      <Input className="mt-1"
                        defaultValue=""
                        placeholder="Password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}>
                      </Input>
                    </FormGroup>
                  </Col>
                </Row>
              </Form>
            </CardBody>
            <CardFooter >
              <Button type="submit" className="btn-fill" color="primary" >
                Login
              </Button>
            </CardFooter>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default Login;
