import React, { useState, useRef } from "react";

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  FormGroup,
  Form,
  Input,
  Row,
  Col,
} from "reactstrap";

import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function AddProduct() {

  const [productname, setProductname] = useState("");
  const [enabled, setEnabled] = useState("");
  const [file, setFile] = useState("");
  const [message, setMessage] = useState("");

  const navigate = useNavigate();
  useEffect(() => {
    const isLoggedIn = localStorage.getItem('isLoggedIn');
    if (!isLoggedIn) {
      navigate('/login');
    }
  }, [navigate]);
  const fileInputRef = useRef(null);

  const handleAddProduct = async (e) => {
    try {
      e.preventDefault();
      const formData = new FormData();
      formData.append('productname', productname);
      formData.append('enabled', enabled);
      formData.append('file', file);

      const response = await fetch('http://localhost:5000/addproduct', {
        method: 'POST',
        body: formData,
      });

      if (response.status === 200) {
        setMessage("Product added successfully.");
        setProductname("");
        setEnabled("");
        setFile(null);

        if (fileInputRef.current) {
          fileInputRef.current.value = null;
        }
      } else {
        const errorData = await response.json();
        setMessage(errorData.message || "An error occurred. Please try again.");
      }
    } catch (error) {
      setMessage(error.message);
    }
  }
  return (
    <>
      <div className="content">
        <Row>
          <Col md="6" className="mx-auto">
            <Card>
              <CardHeader>
                <h5 className="title">Add Product</h5>
              </CardHeader>
              <CardBody>
                <Form  enctype="multipart/form-data">
                  <Row>
                    <Col md="12">
                      <FormGroup>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <label>Product Name</label>
                            <span
                              style={{ cursor: 'pointer', color: 'blue', fontSize: '14px' }}
                              onClick={() => alert('Clicked!')}
                            >
                              + Add New
                            </span>
                          </div>                        <Input
                          placeholder="Ex: abc, pqr, xyz"
                          type="text"
                          value={productname}
                          onChange={(e) => setProductname(e.target.value)}
                          id="productname"
                          name="productname"
                          required
                        />
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>Enabled</label>
                          <Input
                            type="select"
                            className="form-control"
                            name="enabled"
                            id="enabled"
                            value={enabled}
                            onChange={(e) => setEnabled(e.target.value)}
                          >
                            <option value="" disabled>Choose an option</option>
                            <option value="yes">Yes</option>
                            <option value="no">No</option>
                          </Input>
                      </FormGroup>
                    </Col>
                  </Row>
                  <Row>
                    <Col className="pr-md-1" md="6">
                      <FormGroup>
                        <label>Image</label>
                          <Input
                            type="file"
                            className="form-control"
                            name="file"
                            id="file"
                            innerRef={fileInputRef}
                            onChange={(e) => setFile(e.target.files[0])}
                            accept="image/*"
                          >
                            <option value="" disabled>Choose an option</option>
                            <option value="yes">Yes</option>
                            <option value="no">No</option>
                          </Input>
                      </FormGroup>
                    </Col>
                  </Row>
                </Form>
                {message && <div>{message}</div>}
              </CardBody>
              <CardFooter>
                <Button className="btn-fill" color="primary" onClick={handleAddProduct} type="submit">
                  Save
                </Button>

                <br />
                <br />
                <table >
                  <thead>
                    <tr>
                      <th>Product Name</th>
                      <th>Enabled</th>
                      <th>Image</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{productname}</td>
                      <td>{enabled}</td>
                      <td>
                        {file ? (
                          <img
                            src={URL.createObjectURL(file)}
                            alt="Product"></img>
                        ) : (
                          ""
                        )}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default AddProduct;
