import React, { useState } from "react";
// import classNames from "classnames";
// import { Line, Bar } from "react-chartjs-2";
import axios from "axios";
import {
  Button,
  // ButtonGroup,
  Card,
  CardHeader,
  CardBody,
  // CardTitle,
  // DropdownToggle,
  // DropdownMenu,
  // DropdownItem,
  // UncontrolledDropdown,
  // Label,
  FormGroup,
  Form,
  Input,
  // Table,
  Row,
  Col,
  // UncontrolledTooltip,
  CardFooter,
} from "reactstrap";

// core components
// import {
//   chartExample1,
//   chartExample2,
//   chartExample3,
//   chartExample4,
// } from "variables/charts.js";

import bgImage from "assets/img/bg5.jpg";

function Login (props) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");


  const handleLogin = async () => {
    try {
      console.log('Sending login request with:', { username, password });
      const response = await axios.post('http://localhost:5000/login', {
        username: username,
        password: password,
      });
      console.log('Server response:', response.data);
    } catch (error) {
      if (error.response) {
        console.error('Error Response Data:', error.response.data);
        console.error('Error Status:', error.response.status);
        console.error('Error Headers:', error.response.headers);
      } else if (error.request) {
        console.error('No response received:', error.request);
      } else {
        console.error('Error Message:', error.message);
      }
    }
  };

  return (
    <div className="content d-flex justify-content-center align-items-center" style={{ overflowX: "hidden", height: "100vh", backgroundImage: `url(${bgImage})`, backgroundSize: "cover", backgroundPosition: "center", backgroundRepeat: "no-repeat" }}>
      <Row>
        <Col md="6" className="text-center">
          <Card  style={{ width: "400px" }}>
            <CardHeader>
              <h5 >Welcome</h5>
            </CardHeader>
            <CardBody >
              <Form>
                <Row className="justify-content-center">
                  <Col className="pr-md-1" md="8">
                    <FormGroup >
                      <label>Username</label>
                      <Input className="mt-1"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}>
                      </Input>
                    </FormGroup>
                  </Col>
                </Row>
                <Row className="justify-content-center">
                <Col className="pr-md-1" md="8">
                    <FormGroup >
                      <label>Password</label>
                      <Input className="mt-1"
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}>
                      </Input>
                    </FormGroup>
                  </Col>
                </Row>
              </Form>
              {message && <div>{message}</div>}
            </CardBody>
            <CardFooter >
              <Button type="submit" className="btn-fill" onClick={handleLogin} color="primary" >
                Login
              </Button>
              <br />
              <label>Don't have an account? <a href="">Sign Up</a> </label>
            </CardFooter>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default Login;
